import React from 'react';
import ReactDOM from 'react-dom/client';
import Message from "./сomponents/Message"


class App extends React.Component {
  //
  constructor(props) {
    super(props)
    this.state = {
        text: "привет",
    }
    this.click = this.bind.click


  }  
  text = "привdет"
  render () { 
    return <div> 
        <button onClick={this.click} value="нажми на меня" />
        <h1>{this.state.text}</h1>
      </div>
  }
  click () {
    this.setState({
        text: "пока"
    })
  }

}
export default App
