import React from 'react';


 class User extends React.Component {
    constructor (props) {
        super(props)
        this.state = {
            userAnswer: ["", ""],
        }

    }
    users = [
        {
            id: 1,
            question: "2 + 2",
            answer1: 4,
            answer2: 5,
            answer3: 6,
            current: 1,
        },
        {
            id: 2,
            question: "Висит груша, но нельзя скушать",
            answer1: "лампочка",
            answer2: "боксерская груша",
            answer3: "Кабачок",
            current: 2,
        },
     ]

    render() {
   

    return (<div>
     {this.users.map((el) => (
      <div className="task">
        {el.question}
        <br />
        ваш ответ: 
        <select>
        <option value={el.answer1}>{el.answer1}</option>
        <option value={el.answer2}>{el.answer2}</option>
        <option value={el.answer3}>{el.answer3}</option>
        </select>
      </div>
     ))}


    </div>)
    }
 }


 export default User
