import React from 'react';


 class User extends React.Component {
    users = [
        {
            id: 1,
            question: "2 + 2",
            answer1: 4,
            answer2: 5,
            answer3: 6,
            current: 1,
        },
        {
            id: 2,
            question: "Висит груша, но нельзя скушать",
            answer1: "лампочка",
            answer2: "боксерская груша",
            answer3: "Кабачок",
            current: 2,
        },

     ]
    render() {
    return (<div>
     {this.users.map((el) => (
      <div className="task">
        <section>
        <option>1</option>
        <option>2</option>
        <option>3</option>
        </section>
      </div>
     ))}


    </div>)
    }
 }


 export default User
