import ReactDOM from 'react-dom/client';


class Message extends React.Component {
  render () {
    return <h1>{this.props.mes}</h1>
  }
}

class App extends React.Component {
  text = "привет"
  render () {
    return <div> <h1>{this.text}</h1>
    <Message mes="переданное сообщение"/>
      </div>
  }
}
export default App
